import React, { useEffect, useState } from "react"
import { Link, navigate, Router } from "@reach/router"
import axios from "axios"
import GameStat from "./game";

const StatusComponent = () => {
    const [games, setGames] = useState([]);

    useEffect(() => {
        const fetchGames = async () => {
            let result = await axios.get("http://localhost:8200/games") 
            setGames(result.data);
        }

        fetchGames();
    }, [])

    return (
        <div className="bg-light container col-11">
            <h2>Player Status - </h2>
            <ul className="list-inline border-bottom text-center">
                {
                    games.map(game => (
                        <li className="list-inline-item mx-5" key={game._id}>
                            <Link to={"game/" + game._id} className="nav-link text-primary">{game.name}</Link>
                        </li>
                    ))
                }
            </ul>
            <Router>
                <GameStat path="game/:id"/>
            </Router>
        </div>
    )
}

export default StatusComponent
