import React, { useEffect, useState } from "react"
// import { Link } from "@reach/router"
import axios from "axios"

const GameStat = (props) => {
    const [game, setGame] = useState(0);

    useEffect(() => {
        async function getGameStat(id) {
            let result = await axios.get("http://localhost:8200/gameStat/" + id )
            setGame(result.data)
        }
        getGameStat(props.id);
    }, [props.id])

    return (
        <table className="table table-striped table-bordered">
            <thead>
                <tr>
                    <th>Player Name</th>
                    <th className="text-center">Status</th>
                </tr>
            </thead>
            <tbody>
                { game ? game.players.map((player, idx) => (
                    <tr key={idx}>
                        <td>{player.player.name}</td>
                        <td>
                            <button className={"btn mx-3" + player.status === 2 ? "btn-success" : "btn-secondary"}>Playing</button> 
                            <button className={"btn mx-3" + player.status === 1 ? "btn-danger" : "btn-secondary"}>Not Playing</button>
                            <button className={"btn mx-3" + player.status === 0 ? "btn-warning" : "btn-secondary"}>Undecided</button>
                        </td>
                    </tr>
                )) : <tr></tr>}
            </tbody>
        </table>
    )
}

export default GameStat
