import React, { useEffect, useState } from "react"
// import { Link } from "@reach/router"
import axios from "axios"

const GameStat = (props) => {
    const [game, setGame] = useState(0);

    useEffect(() => {
        async function getGameStat(id) {
            let result = await axios.get("http://localhost:8200/gameStat/" + id )
            setGame(result.data)
        }
        getGameStat(props.id);
    }, [props.id])

    return (
        <div className="container col-10">
        <table className="table table-striped table-bordered">
            <thead>
                <tr>
                    <th style={{width: "30%"}}>Player Name</th>
                    <th className="text-center">Status</th>
                </tr>
            </thead>
            <tbody>
                { game ? game.players.map((player, idx) => (
                    <tr key={idx}>
                        <td>{player.player.name}</td>
                        <td className="text-center">
                            <button className={player.status === 2 ? "btn btn-success mx-5 col-2" : "btn btn-secondary mx-5 col-2"}>Playing</button> 
                            <button className={player.status === 1 ? "btn btn-danger mx-5 col-2" : "btn btn-secondary mx-5 col-2"}>Not Playing</button>
                            <button className={player.status === 0 ? "btn btn-warning mx-5 col-2" : "btn btn-secondary mx-5 col-3"}>Undecided</button>
                        </td>
                    </tr>
                )) : <tr></tr>}
            </tbody>
        </table>
        </div>
    )
}

export default GameStat
